--
-- Create POWEREDITOR_USER_TRACKING table for Oracle.
--

create table POWEREDITOR_USER_TRACKING (
	USERNAME varchar2(100) not null,
	USERFIRSTNAME varchar2(100) null,
	USERLASTNAME varchar2(100) null,
	USERDISABLED varchar2(1) not null,
	USERDISABLEDDATE DATE NULL,
	USERLASTLOGINDATE DATE NULL,
	USERCREATEDATE DATE NULL,
	USERLASTMODIFIEDDATE DATE NULL,
	ENABLED_DATE DATE NULL,
	MODIFIEDBY varchar2(100),
	PRIMARY KEY (USERNAME)
);